import React, { useEffect, useState, useMemo, useRef } from 'react';
import './index.css';

const User = () => {
    const [data, setData] = useState([]); 
    const [submitted, setSubmitted] = useState('');
    const [error, setError] = useState(null);
    const filteredRef = useRef(''); 

    useEffect(() => {
        fetch("./json.json")
            .then(response => response.json())
            .then(json => setData(json))
            .catch(() => setError("Failed to load data"));
    }, []);

    const change = (e) => {
        setSubmitted(e.target.value); 
    };

    const addTask = () => {
        if (submitted.trim() !== "") {
            const newTask = {
                name: submitted,
                title: "Default Title",
                description: "Default Description",
                status: "Pending",
            };
            setData([...data, newTask]);
            setSubmitted(""); 
        }
    };

    const deleteTask = (index) => {
        const updatedData = data.filter((_, i) => i !== index); 
        setData(updatedData);
    };

    const editTask = (index) => {
        const editedName = prompt("Edit your task", data[index].description);
        if (editedName) {
            const updatedData = [...data];
            updatedData[index].description = editedName; 
            setData(updatedData);
        }
    };

    // Memoized filtered tasks based on ref value
    const filteredData = useMemo(() => {
        const filterText = filteredRef.current.toLowerCase(); 
        return data.filter(task =>
            task.name.toLowerCase().includes(filterText) || 
            task.description.toLowerCase().includes(filterText) ||
            task.status.toLowerCase().includes(filterText)
        );
    }, [data]); // Only re-filter when data changes

    // Handle the filter change
    const handleFilterChange = (e) => {
        filteredRef.current = e.target.value; // Update the ref value instead of state
    };

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <input
                type="text"
                placeholder="Enter task"
                value={submitted}
                onChange={change}
            />
            <button onClick={addTask}>Add</button>

            <input
                type="text"
                placeholder="Search tasks"
                onChange={handleFilterChange}  // Update the ref value
            />

            <div className='car'>
                {filteredData.map((input, index) => (
                    <div key={index} className='cards'>
                        <h3>{input.name}</h3>
                        <h3>{input.title}</h3>
                        <h3>{input.description}</h3>
                        <h3>{input.status}</h3>
                        <button onClick={() => deleteTask(index)}>Delete</button>
                        <button onClick={() => editTask(index)}>Edit</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default User;
