import React, { useEffect, useState, useMemo } from 'react';
import './index.css';

const User = () => {
    const [data, setData] = useState([]); 
    const [submitted, setSubmitted] = useState('');
    const [error, setError] = useState(null);
    const [filtered, setFiltered] = useState(''); 

    useEffect(() => {
        fetch("./json.json")
            .then(response => response.json())
            .then(json => setData(json))
            .catch(() => setError("Failed to load data"));
    }, []);

    const change = (e) => {
        setSubmitted(e.target.value); 
    };

    const addTask = () => {
        if (submitted.trim() !== "") {
            const newTask = {
                name: submitted,
                title: "Default Title",
                description: "Default Description",
                status: "Pending",
            };
            setData([...data, newTask]);
            setSubmitted("");
        }
    };

    const deleteTask = (index) => {
        const updatedData = data.filter((_, i) => i !== index); 
        setData(updatedData);
    };

    const editTask = (index) => {
        const editedName = prompt("Edit your task", data[index].description);
        
        if (editedName) {
            const updatedData = [...data];
            updatedData[index].description = editedName;
            setData(updatedData);
        }
    };

    
    const filteredData = useMemo(() => {
        return data.filter(task =>
            task.name.toLowerCase().includes(filtered.toLowerCase()) || 
            task.description.toLowerCase().includes(filtered.toLowerCase()) ||
            task.status.toLowerCase().includes(filtered.toLowerCase())
        );
    }, [data, filtered]);

   
    const handleFilterChange = (e) => {
        setFiltered(e.target.value); 
    };

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <input type="text" placeholder="Enter task" value={submitted} onChange={change} />
            <button onClick={addTask}>Add</button>

            <input type="text" placeholder="Search tasks" value={filtered} onChange={handleFilterChange}  />

            <div className='car'>
                {filteredData.map((input, index) => (
                    <div key={index} className='cards'>
                        <h3>{input.name}</h3>
                        <h3>{input.title}</h3>
                        <h3>{input.description}</h3>
                        <h3>{input.status}</h3>
                        <button onClick={() => deleteTask(index)}>Delete</button>
                        <button onClick={() => editTask(index)}>Edit</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default User;
