   const [error,setError] = useState(null)
    useEffect(()=>{        
   fetch("./json.json ")
    .then(response=>response.json())
    .then(json=>setData(json))
    .catch(error=>setError("failed to load data"))
    },[])
    const deleteTask =(indexValue)=>{
        const nezz = submitted.filter((todo,index ) => index !== indexValue)
        setSubmitted(nezz)
    }
    const editTask = (index)=>{
      const kkk = [...submitted];
      kkk[index] = prompt("edit your task",kkk[index])
      setSubmitted(kkk)
    }
    if (error){
        return(
            <div>{error}</div>
        )
    }
    return (
        <div className='car'>
       {data.map((input,index)=>(
        <div key={index} className='cards'>
            <h3>{input.name}</h3>
            <h3>{input.title}</h3>
            <h3>{input.description}</h3>
            <h3>{input.status}</h3>
            <button onclick={()=>handle(input)}>edit</button>
            <button onclick={()=>edit(index)}>delete</button>

        </div>

       ))}
            
        </div>
    )
}
export default User
