import React, { useEffect, useState } from 'react'
import './index.css'
 const User = () => {
    const [data,setData] = useState()
    const [submitted, setSubmitted] = useState([]);
   const [error,setError] = useState(null)
    useEffect(()=>{        
   fetch("./json.json ")
    .then(response=>response.json())
    .then(json=>setData(json))
    .catch(error=>setError("failed to load data"))
    },[])
    const handleChange = (e) => {
        setData(e.target.value);
      };
      const manii = (e) => {
        e.preventDefault();
        const newww =[...submitted,inputValue]
        setSubmitted(newww); 
         setInputValue('');
      };

    const handle =(indexValue)=>{
        const nezz = submitted.filter((input ) => input !== indexValue)
        setSubmitted(nezz)
    }
    const edit = (index)=>{
      const kkk = [...submitted];
      kkk[index] = prompt("edit your task",kkk[index])
      setSubmitted(kkk)
    }
    if (error){
        return(
            <div>{error}</div>
        )
    }
    return (
        <div className='car'>

          <input type="text" value={data} placeholder='plese enter the text' onChange={handleChange} />
          <button className='queen' onClick={manii}>Add task</button>

       {submitted.map((input,index)=>(
        <div key={index} className='cards'>
            <h3>{input.name}</h3>
            <h3>{input.title}</h3>
            <h3>{input.description}</h3>
            <h3>{input.status}</h3>
            <button onclick={()=>handle(input)}>edit</button>
            <button onclick={()=>edit(index)}>delete</button>

        </div>

       ))}
            
        </div>
    )
}
export default User
