import React, { useEffect, useState } from 'react'
import './index.css'
 const User = () => {
    const [data,setData] = useState([])
    const [submitted, setSubmitted] = useState();
   const [error,setError] = useState(null)
    useEffect(()=>{        
   fetch("./json.json ")
    .then(response=>response.json())
    .then(json=>setData(json))
    .catch(error=>setError("failed to load data"))
    },[])
    const change=(e)=>{
        setData(e.target.value)
    }
    const handle =(indexValue)=>{
        const nezz = submitted.filter((input ) => input !== indexValue)
        setData(nezz)
    }
    const edit = (index)=>{
      const kkk = [...submitted];
      kkk[index] = prompt("edit your task",kkk[index])
      set(kkk)
    }
    if (error){
        return(
            <div>{error}</div>
        )
    }
    return (
        <div className='car'>

          <input type="text" placeholder='' value={data} onChange={change} />

       {data.map((input,index)=>(
        <div key={index} className='cards'>
            <h3>{input.name}</h3>
            <h3>{input.title}</h3>
            <h3>{input.description}</h3>
            <h3>{input.status}</h3>
            <button onclick={()=>handle(input)}>delete</button>
            <button onclick={()=>edit(index)}>edit</button>

        </div>

       ))}
            
        </div>
    )
}
export default User
