import React, { useState } from 'react';
import './login.css'; 
import { FaFacebookF, FaGoogle } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navi=useNavigate();

  const handle = (e) => {
    e.preventDefault();
    alert(`Successfully submitted: Email: ${email}, Password: ${password}`);
    setEmail("")
    setPassword("")
  };

  const handlePassword=()=>{
    navi('/Reset')
  }
  const arrow=()=>{
    navi('/Signup')
  }

  return (
    <div className="signup">
      <div>
      <span className="back" onClick={arrow}>&lt;</span>
      </div>
      <h1 className='pk'>Login</h1>
      <form>
        <label>E-mail</label>
        <input
          type="email"
          value={email}
          placeholder="Enter the email"
          onChange={(e) => setEmail(e.target.value)}
        />

        <label>Password</label>
        <div className="password">
          <input
            type="password"
            value={password}
            placeholder="********"
            onChange={(e) => setPassword(e.target.value)}
          />
          <span className="eye-icon">&#128065;</span>
        </div>

       <p className='for' onClick={handlePassword}>forget password ?</p>

        <button onClick={handle} type="submit" className="signup-btn">
          Login
        </button>
      </form>

      <p className="login-text">
        Don't have an account? <a href="/sign-up">Sign up</a>
      </p>

      <div className="social-signup">
        <p className='po'>Sign up with</p>
        <div className="social">
        <button className="fb">
            <a href="https://www.facebook.com/login/">
            <FaFacebookF className="icon" /> Facebook
            </a></button>
          <button className="gg">
            <a href="https://www.google.com/intl/en-GB/account/about/">
            <FaGoogle className="icons" /> Google
            </a>  </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
