import React, { useState } from 'react';  // Corrected import statement

const Verify = () => {
  const [verificationCode, setVerificationCode] = useState(''); // useState hook to manage the code

  // Function to generate a random 4-digit code
  const generateCode = () => {
    const code = Math.floor(1000 + Math.random() * 9000); // Generates a number between 1000 and 9999
    setVerificationCode(code); // Sets the generated code in the state
  };

  return (
    <div>
      <div>
        <span className="back">&lt;</span> {/* Back arrow */}
      </div>
      <div>
        <h1>Verification Code</h1>
        <p>Please type the verification code sent to prelookstudio@gmail.com</p>
      </div>
      <div>
        <h2>Your Verification Code</h2>
        <p>{verificationCode || '---'}</p> {/* Displays the code or a placeholder if not generated */}
        <button onClick={generateCode}>Generate Code</button>
      </div>
    </div>
  );
};

export default Verify;
