import React, { useState } from 'react';
import './Todo.css'

const Todo = () => {
   const [inputValue, setInputValue] = useState();
 const [submitted, setSubmitted] = useState([]);

 
  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

 const manii = (e) => {
  e.preventDefault();
  if(!inputValue){
    alert("enter the task")
    return;
  }
     
    const newww =[...submitted,inputValue]
    setSubmitted(newww); 
     setInputValue('');
  };

  const deleteTask =(indexValue)=>{
      const nezz = submitted.filter((todo,index ) => index !== indexValue)
      setSubmitted(nezz)
  }
  const editTask = (index)=>{
    const kkk = [...submitted];
    kkk[index] = prompt("edit your task",kkk[index])
    setSubmitted(kkk)
  }

  const toggleCompletion = (index) => {
    setSubmitted(
      submitted.map((todo) =>
        todo.id === index ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  return (
    <div className='mani'>
        <h1>todo app</h1>
      <form >
         <input type="text" value={inputValue} placeholder='enter the task' onChange={handleChange}/>
         <button className='queen' onClick={manii}>Add task</button>
      </form>
        
      <ul>
    
       { submitted.map((todo,index) => (
          
          <li key ={index} className={todo.completed ? 'completed' : 'null'} >
            
            <p>{todo}</p>
            <button className='king' onClick={() => deleteTask(index)}>Delete</button>
          <button className='king' onClick={() => editTask(index)}>edit</button> 
          <button  onClick={() => toggleCompletion(index)}>
                {todo.completed ? 'Undo' : 'Complete'}
              </button>
          </li>
         
        ))}
        
      </ul>
       </div>
       
  );
};

export default Todo;
