import React, { useEffect, useState } from "react";
import "./home.css";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]); // For filtered items
  const [selectedCategory, setSelectedCategory] = useState("All"); // Active category

  useEffect(() => {
    fetch("/date.json")
      .then((response) => response.json())
      .then((json) => {
        setData(json);
        setFilteredData(json); // Initialize filtered data
      });
  }, []);

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    if (category === "All") {
      setFilteredData(data); // Show all items
    } else {
      setFilteredData(data.filter((item) => item.category === category)); // Filter by category
    }
  };

  const handleFoodDetails = () => {
    navigate("/FoodDetails");
  };

  return (
    <div className="he">
      <div className="head">
        <img src="./asset/Group (1).png" alt="pic" className="mb" />
        <span className="war">Deliver To</span>
        <img src="./asset/Group (2).png" alt="pi" className="mbd" />
      </div>

      <div>
        <h4>What Would You Like</h4>
        <br />
      </div>

      <div>
        <input
          className="asd"
          type="text"
          placeholder="Find for food or restaurant"
        />
      </div>

      <div className="categories">
        {["All", "Burger", "Pizza", "Donut", "Mexican"].map((category) => (
          <button
            key={category}
            className={`category-button ${
              selectedCategory === category ? "active" : ""
            }`}
            onClick={() => handleCategoryClick(category)}
          >
            {category}
          </button>
        ))}
      </div>

      <div className="mapped">
        {filteredData.map((val, index) => (
          <div key={index} className="ima">
            <img src={val.image} alt="" width={"60px"} />
            <h5>{val.name}</h5>
          </div>
        ))}
      </div>

      <div className="fla">
        <h3 className="plk">Featured Restaurant</h3>
        <p className="ak">View All</p>
      </div>

      <div className="khan">
        <img
          src="./asset/last.png"
          alt="piu"
          width={"200px"}
          onClick={handleFoodDetails}
        />
        <img src="./asset/two.png" alt="bur" width={"200px"} />
      </div>

      <div className="icons">
        <img src="./asset/three.png" alt="pol" width={"20px"} />
        <img src="./asset/four.png" alt="pol" width={"20px"} />
        <img src="./asset/five.png" alt="pol" width={"20px"} />
        <img src="./asset/six.png" alt="pol" width={"20px"} />
        <img src="./asset/three.png" alt="pol" width={"20px"} />
      </div>
    </div>
  );
};

export default Home;
