import { useEffect,useState,useav } from "react";

const Home = () => {


    const navigate = useNavigate();
    const [data, setData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
  
    useEffect(() => {
      fetch("/date.json")
        .then((response) => response.json())
        .then((json) => {
          setData(json);
          setFilteredData(json);
        });
    }, []);
  
    const handleSearchChange = (event) => {
      const value = event.target.value.toLowerCase();
      setSearchTerm(value);
  
      const filtered = data.filter((item) => {
        const name = item.name?.toLowerCase() || "";
        const category = item.category?.toLowerCase() || "";
        return name.includes(value) || category.includes(value);
      });
      setFilteredData(filtered);
    };
  
    // Navigate to specific movie pages
    const handleMovieClick = (id) => {
      navigate(`/One/${id}`);
    };
  
    const handleProfile = () => {
      navigate("/Profile");
    };
  
    const handleLogout = () => {
      navigate("/Logout");
    };
  
    const handleViewAll = () => {
      navigate("/Fav");
    };
  
    return (
      <div className="he">
        <div className="head">
          <img
            src="/assest/Group (1).png"
            alt="pic"
            className="mb"
            onClick={handleLogout}
          />
          <span className="war">BOOK MY SHOW</span>
          <img
            src="/assest/download.jpg"
            alt="pi"
            className="mbd"
            onClick={handleProfile}
          />
        </div>
  
        <div>
          <h4>What MOVIES Would You Like</h4>
          <br />
        </div>
  
        <div>
          <input
            className="asd"
            type="text"
            placeholder="Find your movies"
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
  
        <div className="mapped">
          {filteredData.length > 0 ? (
            filteredData.map((val) => (
              <div
                key={val.id}
                className="ima"
                onClick={() => handleMovieClick(val.id)} // Pass movie ID
              >
                <img src={val.image} alt={val.name} width={"60px"} />
                <h5>{val.name}</h5>
              </div>
            ))
          ) : (
            <p>No results found.</p>
          )}
        </div>
  
        <div className="fla">
          <h3 className="plk">Thriller movies</h3>
          <p className="ak" onClick={handleViewAll}>
            View All
          </p>
        </div>
  
        <div className="khan">
          <img src="/assest/download (2).jpg" alt="" className="ji" />
          <img src="/assest/download (3).jpg" alt="" className="yi" />
          <img src="/assest/download (4).jpg" alt="" className="lp" />
          <img src="/assest/download (3).jpg" alt="" className="yi" />
        </div>
      </div>
    );
  };
  
  export default Home;
