import React, { useEffect, useState } from "react";
import "./index.css";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]); // For filtered items
  const [searchTerm, setSearchTerm] = useState(""); // Search input

  useEffect(() => {
    fetch("/date.json")
      .then((response) => response.json())
      .then((json) => {
        setData(json);
        setFilteredData(json); // Initialize filtered data
      });
  }, []);

  const handleSearchChange = (event) => {
    const value = event.target.value.toLowerCase();
    setSearchTerm(value);

    // Filter data based on the search term
    const filtered = data.filter((item) => {
      const name = item.name?.toLowerCase() || "";
      const category = item.category?.toLowerCase() || ""; // Ensure `category` is a string
      return name.includes(value) || category.includes(value);
    });
    setFilteredData(filtered);
  };


  const handlejk=()=>{
    navigate("/One")
}

   const handleacf=()=>{
    navigate("/Profile")
   }
   
   const handler=()=>{
    navigate("/Logout")
   }

  return (
    <div className="he">
      <div className="head">
        <img src="/assest/Group (1).png" alt="pic" className="mb" onClick={handler} />
        <span className="war">BOOK MY SHOW</span>
        <img src="/assest/download.jpg" alt="pi" className="mbd" onClick={handleacf}/>
      </div>

      <div>
        <h4>What MOVIES Would You Like</h4>
        <br />
      </div>

      <div>
        <input
          className="asd"
          type="text"
          placeholder="Find your movies"
          value={searchTerm} 
          onChange={handleSearchChange} 
        />
      </div>

      <div className="mapped">
  {filteredData.length > 0 ? (
    filteredData.map((movie) => (
      <div key={movie.id} className="ima" onClick={() => handleMovieClick(movie.id)}>
        <img src={movie.image} alt={movie.name} width={"60px"} />
        <h5>{movie.name}</h5>
      </div>
    ))
  ) : (
    <p>No results found.</p>
  )}
</div>

      <div className="fla">
        <h3 className="plk">Thriller movies</h3>
      
      </div>

      <div className="khan">
        <img src="/assest/download (2).jpg" alt=""  className="ji"/>
        <img src="/assest/download (3).jpg" alt="" className="yi"/>
        <img src="/assest/download (4).jpg" alt="" className="lp" />
        <img src="/assest/download (3).jpg" alt="" className="yi"/>
       
      </div>

    
    </div>
  );
};

export default Home;
