import React, { useState } from "react";

import { useNavigate } from "react-router-dom";

import "./app.css";
 
const Main = ({ datasetGroupName }) => {

  const [data, setData] = useState([]); // Stores groups

  const [showPopup, setShowPopup] = useState(false); // Controls popup visibility

  const [groupName, setGroupNameLocal] = useState(""); // Local group name state

  const [selectedColor, setSelectedColor] = useState(""); // Tracks the selected color

  const navigate = useNavigate();
 
  const handleAddGroup = () => {

    setShowPopup(true);

  };
 
  const handleCreateGroup = () => {

    if (groupName && selectedColor) {

      setData([...data, { name: groupName, color: selectedColor }]);

      setGroupNameLocal("");

      setSelectedColor("");

      setShowPopup(false);

    } else {

      alert("Please enter a group name and select a color.");

    }

  };
 
  const handleNavigateToGroup = (name) => {

    setGroupName(name); // Update global groupName state

    navigate(`/Html/${name}`);

  };
 
  return (
<div className="main">
<ul className="youell">
<h2>POCKET NOTES</h2>

        {data.map((group, index) => (
<li key={index}>
<p

              onClick={() => handleNavigateToGroup(group.name)}

              style={{

                backgroundColor: group.color,

                padding: "5px",

                borderRadius: "5px",

              }}
>

              {group.name}
</p>
</li>

        ))}
<button onClick={handleAddGroup} className="but">

          +
</button>
</ul>
 
      {showPopup && (
<div className="popup">
<input

            type="text"

            value={groupName}

            onChange={(e) => setGroupNameLocal(e.target.value)}

            placeholder="Enter group name"

          />
<div className="color-options">

            {["#FF5733", "#33FF57", "#3357FF", "#FF33A8", "#F4FF33"].map(

              (color) => (
<div

                  key={color}

                  className={`color-box ${

                    selectedColor === color ? "selected" : ""

                  }`}

                  style={{ backgroundColor: color }}

                  onClick={() => setSelectedColor(color)}

                />

              )

            )}
</div>
<button  className="bb"onClick={handleCreateGroup}>Create</button>
<button className="vv" onClick={() => setShowPopup(false)}>Cancel</button>
</div>

      )}
 
      <div className="Pocket">
<img src="./asset/pp.png" alt="" style={{ width: "50%" }} />
<h1>Pocket Notes</h1>
<p>

          Send and receive messages without keeping your phone online. <br />

          Use Pocket Notes on up to 4 linked devices and 1 mobile phone.
</p>
</div>
</div>

  );

};
 
export default Main;
