import React, { useState } from "react";

import { useParams } from "react-router-dom";
 
const GroupPagr = () => {
  const { groupName } = useParams(); // Get the dynamic group name from the URL
  const [inputValue, setInputValue] = useState(""); // State to store input value
 
  return (
<div className="group-page">
<h1>Group: {groupName}</h1>
<p>Welcome to the {groupName} group page!</p>
 
      {/* Paragraph displays the input value */}
<div className="content">
<p className="output-text">{inputValue}</p>
</div>
 
      {/* Input box positioned at the bottom */}
<div className="input-container">
<input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)} // Update state on input change
          placeholder="Type something here..."
        />
</div>
</div>
  );
};
 
export default GroupPage;
