import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Main from "./Figma/Main";
import Html from "./Figma/Html";

function App() {
  const [groupName, setGroupName] = useState(""); // Store groupName globally

  return (
<BrowserRouter>
<Routes>
<Route
          path="/"
          element={<Main setGroupName={setGroupName} />} // Pass setter to Main
        />
<Route
          path="/Go:groupName"
          element={<Html groupName={groupName} />} // Pass groupName to Html
        />
</Routes>
</BrowserRouter>
  );
}

export default App;
