import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import './index.css'

 
const Firstt = () => {
  const [data, setData] = useState([]); 
  const [showPopup, setShowPopup] = useState(false); 
  const [groupName, setGroupName] = useState(""); 
  const [selectedColor, setSelectedColor] = useState(""); 
  const navigate = useNavigate();
 
  const handleAddGroup = () => {
    setShowPopup(true);
  };
 
  const handleCreateGroup = () => {
    if (groupName && selectedColor) {
      setData([...data, { name: groupName, color: selectedColor }]);
      setGroupName("");
      setSelectedColor("");
      setShowPopup(false);
    } else {
      alert("Please enter a group name and select a color.");
    }
  };
 
  const handleNavigateToGroup = (groupName) => {
    navigate(`/group/${groupName}`);
  };
 
  return (

<div className="main">
    
<ul className="youell">
<h1>MY NOTES</h1>
        {data.map((group, index) => (
<li key={index}>
<p
              onClick={() => handleNavigateToGroup(group.name)}
              style={{ backgroundColor: group.color, padding: "5px", borderRadius: "5px" }}
>
              {group.name}
</p>
</li>
        ))}
<button onClick={handleAddGroup}>+</button>
</ul>
 
      {showPopup && (
<div className="popup">
<input
            type="text"
            value={groupName}
            onChange={(e) => setGroupName(e.target.value)}
            placeholder="Enter group name"
          />
<div className="color-options">
            {["#FF5733", "#33FF57", "#3357FF", "#FF33A8", "#F4FF33"].map((color) => (
<div
                key={color}
                className={`color-box ${selectedColor === color ? "selected" : ""}`}
                style={{ backgroundColor: color }}
                onClick={() => setSelectedColor(color)}
              />
            ))}
</div>
<button onClick={handleCreateGroup}>Create</button>
<button onClick={() => setShowPopup(false)}>Cancel</button>
</div>
      )}
      <div >
        <img src={'./asset/pp.png'} alt="" style={{width:""}}/>
      </div>
</div>
  );
};
 export default Firstt
