import React, { useState } from "react";

import "./app.css";


 
const Html = ({ groupName }) => {

  const [Input, setInput] = useState("");
  const [update, setUpdate] = useState([]);
  const handleInput = (e) => {

    setInput(e.target.value);

  };
 
  const handleInputChange = () => {
  setUpdate((prev) => [...prev, Input]);
  setInput("");

  };
 
  return (
<div className="group-page">
<h1>{groupName}</h1>
<p className="oo">Another productive way to use this tool to begin a daily writing routine. <br></br>One way is to generate a random paragraph with the intention to try to rewrite it while still.<br></br> The purpose here is to just get the writing started so that when the writer goes onto their day's <br></br> words are already flowing from their fingers.</p>
 
      <div className="content-container">
       <div className="paragraphs">

          {update.map((item, index) => (
         <p key={index} className="paragraph-item">
               {item}
               </p>
              ))}
               </div>
 
        <div className="input-container">
          <input type="text" value={Input} onChange={handleInput} placeholder="Write something..." className="text-input"

          />
<button onClick={handleInputChange} className="input-button">
add
</button>
</div>
</div>
</div>

  );

};
 
export default Html;
